/*
 * Creates the topbar toggle menu
 * 
 */(function(e){e(document).ready(function(){var t=e(".js-topbar-user"),n=e(".js-topbar-search"),r=e(".js-topbar-link");r.on("click touchstart",function(i){var s=e(this);if(s.hasClass("topbar-link-user")){n.hide();t.toggle();e(".js-topbar-user #edit-name").focus()}if(s.hasClass("topbar-link-search")){t.hide();n.toggle();e(".js-topbar-search #edit-search-block-form--2").focus()}if(s.hasClass("active")){s.removeClass("active");r.one("touchend",function(){s.css("background-color","inherit")})}else{s.css("background-color","");r.removeClass("active");s.addClass("active")}i.preventDefault()});t.hide();n.hide()})})(jQuery);